package ud0;

public class Operaciones {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =15;
		double b =17.5;
		
		System.out.println("a" + " + " + "b" + " = " + (a + b));
		System.out.println("a" + " - " + "b" + " = " + (a - b));
		System.out.println("a" + " * " + "b" + " = " +  a * b);
		System.out.println("a" + " / " + "b" + " = " +  a / b);

		
		System.out.println("Suma : "+"a" + " + " +"b" + " = " + (a + b)+" Resta : " + "a" + " - " +"b" + " = " + (a - b) + " Multi :" +"a" + " * " + "b" + " = " + a * b + " Division :"+ "a" + " / " + "b" + " = " + a / b);
		
		
		

	}

}
